use itertools::Itertools;
use std::ops::{Div, Sub};

pub struct Calc;

impl Calc {
    pub fn add(arr: Vec<f64>) -> f64 {
        arr.iter().sum::<f64>()
    }

    pub fn sub(arr: Vec<f64>) -> f64 {
        arr.iter().map(|&x| x as f64).fold1(Sub::sub).unwrap_or(0.0)
    }

    pub fn mul(arr: Vec<f64>) -> f64 {
        arr.iter().product()
    }

    pub fn div(arr: Vec<f64>) -> f64 {
        arr.iter().map(|&x| x as f64).fold1(Div::div).unwrap_or(0.0)
    }
}

#[test]
fn test_all_operations() {
    // addition
    assert_eq!(Calc::add([2.0, 4.0, 6.0].to_vec()), 12.0);
    assert_eq!(Calc::add([-6.0, 5.0, 10.0].to_vec()), 9.0);

    // subtraction
    assert_eq!(Calc::sub([10.0, 4.0, 6.0].to_vec()), 0.0);
    assert_eq!(Calc::sub([100.0, 10.0, 19.0].to_vec()), 71.0);

    // multiplication
    assert_eq!(Calc::mul([10.0, 10.0, 2.0].to_vec()), 200.0);
    assert_eq!(Calc::mul([-3.0, 2.0].to_vec()), -6.0);

    // division
    assert_eq!(Calc::div([54.0, 2.0, 3.0].to_vec()), 9.0);
    assert_eq!(Calc::div([4.0, 2.0, 5.0].to_vec()), 0.4);
}

mod calc;
use calc::Calc;
use std::io;

fn main() {
    println!("Welcome to the a basic calculator built with Rust.");

    loop {
        println!("Please enter an equation or \"q\" to quit: ");

        let mut input = String::new();
        io::stdin()
            .read_line(&mut input)
            .expect("Failed to read input");

        if input.trim() == "q" {
            println!("Thanks for using this program.");
            break;
        }

        let valid_operators = vec!["+", "-", "*", "/"];

        for operator in valid_operators {
            match input.find(operator) {
                Some(_) => {
                    let parts: Vec<&str> = input.split(operator).collect();

                    if parts.len() < 2 {
                        panic!("Invalid equation.");
                    }

                    let mut number_array = vec![];
                    let mut counter = 0;

                    while counter != parts.len() {
                        let val: f64 = parts[counter].trim().parse().ok().expect("Enter a number.");
                        number_array.push(val);
                        counter += 1;
                    }

                    match operator {
                        "+" => println!("{}", Calc::add(number_array)),
                        "-" => println!("{}", Calc::sub(number_array)),
                        "*" => println!("{}", Calc::mul(number_array)),
                        "/" => println!("{}", Calc::div(number_array)),
                        _ => println!("Only addition, subtraction, multiplication and division are supported.")
                    }
                }

                None => {
                    continue;
                }
            }
        }
    }
}
